import React from 'react';

import '../node_modules/@material-ui/core/umd/material-ui.development.js';
import '../node_modules/@material-ui/core/umd/material-ui.production.min.js';

import './App.css';
import Show from './components/show';
import Nav from './components/nav';
import {makeStyles} from '@material-ui/core/styles';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import Button from '@material-ui/core/Button';

import Form from './components/form';


const useStyles = makeStyles(theme => ({
    root: {
        padding: theme.spacing(3, 2),
        backgroundColor: '#607d8b',
        color: 'white'
    },
    formControl: {
        margin: theme.spacing(1)
    }
}));
const App = props => {
    const [open, setOpen] = React.useState(false);
    const classes = useStyles();
    const [newItem, setNewItem] = React.useState({title: '', text: ''});
    console.log(newItem);

    const handleOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };
    return (
        <div className="App">
            <Nav handleOpen={handleOpen}/>
            <Show store={
                props.store
            }/>
            <Dialog open={open}
                onClose={handleClose}
                aria-labelledby="alert-dialog-title"
                aria-describedby="alert-dialog-description"
                minWidth='md'>
                <DialogTitle id="alert-dialog-title">
                    {"Adding new item"}</DialogTitle>
                <DialogContent>
                    <DialogContentText id="alert-dialog-description">
                        <Form setNewItem={setNewItem}/>
                    </DialogContentText>
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleClose}
                        color="primary">
                        Cancel
                    </Button>
                    <Button onClick={handleClose}
                        color="primary"
                        autoFocus>
                        ADD
                    </Button>
                </DialogActions>
            </Dialog>
        </div>
    );
}

export default App;
