import React from 'react';
import Container from '@material-ui/core/Container';
import {makeStyles} from '@material-ui/core/styles';

import Cart from './cart';
import del from '../actions/edit';

const Show = props => {
    const useStyless = makeStyles(theme => ({
        padd: {
            padding: theme.spacing(3, 2)
        },
    }));
    // const [i, setI] = useState(1);

    const submiter = (input, id=null, item={}) => {
        switch (input) {
            case 'DELETE':
                props.store.dispatch(del(id));
                break;
            case 'EDIT':
                props.store.dispatch(item);
                break;
            default:
                return;
        }
    };
    const classes = useStyless();

    const handleDel = id => {
        console.log(id);
        submiter('DELETE', id);
    }
    const handleEdit = item => {
        // if (item)
        // {console.log("let's edit");}
    }
    return (
        <React.Fragment>
            <Container className={
                    classes.padd
                }
                >

                {
                props.store.getState().map((item, i) => (
                    <Cart handleDel={handleDel} handleEdit={handleEdit} item ={item} key={i}/>
                ))
            } </Container>
        </React.Fragment>
    );
}

export default Show;
