import React, {useState} from 'react';

import '../node_modules/@material-ui/core/umd/material-ui.development.js';
import '../node_modules/@material-ui/core/umd/material-ui.production.min.js';

import './App.css';
import Show from './components/show';
import Nav from './components/nav';
import add from './actions/add';
import {TextField, Box, Button} from '@material-ui/core';
import {makeStyles} from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
    container: {
        display: 'flex',
        flexWrap: 'wrap'
    },
    textField: {
        marginLeft: theme.spacing(1),
        marginRight: theme.spacing(1),
        width: 200
    },
    root: {
        display: "flex",
        flexDirection: "column",
        justifyContent: "flex-start",
        alignItems: 'center'
    }
}));

const App = props => {
    const classes = useStyles();
    const i = 0;
    const [newItem, setNewItem] = React.useState({id: i, title: '', text: ''});
    const [text, setText] = useState('');
    const [name, setName] = useState('');

    const handleText = event => {
        setText(event.target.value);
        setNewItem(Object.assign({}, newItem, {text: text}))
    }
    const handleName = event => {
        setName(event.target.value);
        setNewItem(Object.assign({}, newItem, {title: name}))
    }
    const handleClick = () => {
        props.store.dispatch(add(newItem));
    }
    return (
        <div className="App">
            <Nav/>
            <Box postion="static">
                <TextField id="outlined-textarea" label="Title" placeholder="Memorizable thing ;)"
                    className={
                        classes.textField
                    }
                    value={
                        newItem.title
                    }
                    margin="normal"
                    variant="outlined"
                    onChange={handleName}/>
                <TextField id="outlined-textarea" label="Text" placeholder="some word !"
                    className={
                        classes.textField
                    }
                    value={
                        newItem.text
                    }
                    multiline
                    margin="normal"
                    variant="outlined"
                    onChange={handleText}/>
                <Button onClick={handleClick}>Add</Button>
            </Box>
            <Show store={
                props.store
            }/>
        </div>
    );
}

export default App;
