import React, {useState} from 'react';
import Container from '@material-ui/core/Container';
import {makeStyles} from '@material-ui/core/styles';

import ToDo from './todo';
import {addTodo, delTodo} from '../actions/index';
import {
    TextField,
    Button,
    Box
} from '@material-ui/core';

const Show = props => {
    const [elm, setElm] = useState('');
    const [stt, setStt] = useState(0);

    const store = props.store 

    function componentDidMount() {
        store.subscribe(() => console.log(props.store.getState()));
        setStt(1)
    }




    const useStyless = makeStyles(theme => ({
        padd: {
            padding: theme.spacing(3, 2),
            display: 'flex',
            justifyContent: 'center',
            flexDirection: 'column',
            alignItems: 'center'
        }
    }));

    const classes = useStyless();
    const handleDel = id => {
        console.log("Remove button is clicked")
        console.log(`id: ${id}`)
        props.store.dispatch(delTodo(id));
    }
    const handleEdit = id => {

    }
    const handleAdd = () => {
        console.log("Add button Clicked")
        props.store.dispatch(addTodo({id: props.store.getState().length, content: elm, visiblity: true}));
        setElm('')
                            }
    const handleChange = event => {
        console.log("change is affected")
        setElm(event.target.value);
    }
    return (
        <React.Fragment>
            <Container className={
                classes.padd
            }>
                <Box position='static' display='flex' flexDirection='row'>
                    <TextField id="normal-textarea" label="ToDo ..." placeholder="Memorizable thing ;)"
                        value={elm}
                        margin="normal"
                        onChange={
                            handleChange    
                        }/>
                    <Button variant='outlined' size="small" color='primary' 
                        onClick={
                            handleAdd
                    }>
                        Add
                    </Button>
                </Box>
                {
                store.getState().map((item, i) => {
                    return (
                        <ToDo item={item}
                            id={i}
                            isEditing={false}
                            handleEdit={handleEdit}
                            handleDel={handleDel}/>
                    )
                })
            } </Container>
        </React.Fragment>
    );
}

export default Show;
