const initState = [
    {
        id: 1,
        title: "this is Title",
        content: "this is content"
    },
];

const doThis = (action, state = initState) => {
    console.log(action, action.type);
    switch(action.type){
        case 'ADD_TODO':
            let updated = Object.assign([], state);
            updated.push(action.item);
            return updated;
        case 'DELETE_TODO':
            let reducet = Object.assign([], state);
            reducet = reducet.filter(item => (item.id !== action.id));
            return reducet;
        case 'EDIT_TODO':
            let edited = Object.assign([], state);
            edited.forEach((item, i) => {
                if( item.id === i ){
                    item.title = (action.title)? item.title : action.title ;
                    item.content = (action.content)? item.content : action.content ;
                }
            });
            return edited;
        default:
            return state;
    }
}
export default doThis;
