import React, {useState} from 'react';
import Container from '@material-ui/core/Container';
import {makeStyles} from '@material-ui/core/styles';

import ToDo from './todo';
import {Types, addTodo, delTodo, editTodo} from '../actions/index';
import {
    TextField,
    Card,
    CardContent,
    Typography,
    Button,
    CardActions,
    Box
} from '@material-ui/core';

const Show = props => {

    function componentDidMount() {
        props.store.subscribe(() => console.log(props.store.getState()))
    }

    const useStyless = makeStyles(theme => ({
        padd: {
            padding: theme.spacing(3, 2),
            display: 'flex',
            justifyContent: 'center',
            flexDirection: 'column',
            alignItems: 'center'
        }
    }));

    const classes = useStyless();
    const handelChange = event => {
        console.log(event);
    }

    return (
        <React.Fragment>
            <Container className={
                classes.padd
            }>
                <Box position='static'>
                    <TextField id="normal-textarea" label="ToDo ..." placeholder="Memorizable thing ;)"
                        value={
                            item.content
                        }
                        margin="normal"
                        onChange={handleChange}/>
                </Box>
                {
                props.store.getState().map((item, i) => {
                    console.log(item)
                    return (
                        <ToDo item={item}
                            key={i}
                            isEditing={false}
                            handelChange={handelChange}/>
                    )
                })
            } </Container>
        </React.Fragment>
    );
}

export default Show;
