    //             // }));
    //             setI(i + 1);
    //             break;
    //         default:
    //             return;
    //     }
    // };
    const classes = useStyless();
    return (
        <React.Fragment>
            <Container className={
                    classes.padd
                }
                maxWidth="sm">

                {
                props.store.getState().map((item) => (
                    <Cart item ={item} />
                ))
            } </Container>
        </React.Fragment>
    );
}

export default Show;
