import React from 'react';

import '../node_modules/@material-ui/core/umd/material-ui.development.js';
import '../node_modules/@material-ui/core/umd/material-ui.production.min.js';

import './App.css';
import Show from './components/show';
import Nav from './components/nav';
import add from './actions/add';
import {TextField, Box, Button, Input} from '@material-ui/core';


const App = props => {
    const i = 0;
    const [newItem, setNewItem] = React.useState({id: i, title: '', text: ''});
    const [text, setText] = useState('');
    const [name, setName] = useState('');

    const handleText = event => {
        setText(event.target.value);
        props.setNewItem(Object.assign({}, props.newItem, {text: text}))
    }
    const handleName = event => {
        setName(event.target.value);
        props.setNewItem(Object.assign({}, props.newItem, {title: name}))
    }
    const handleClick = () => {
        props.store.dispatch(add(newItem));
    }
    return (
        <div className="App">
            <Nav/>
            <Box postion="static">
                <TextField id="outlined-textarea" label="Title" placeholder="Memorizable thing ;)"
                    className={
                        classes.textField
                    }
                    value={
                        newItem.title
                    }
                    margin="normal"
                    variant="outlined"
                    onChange={handleName}/>
                <TextField id="outlined-textarea" label="Text" placeholder="some word !"
                    className={
                        classes.textField
                    }
                    value={
                        newItem.text
                    }
                    multiline
                    margin="normal"
                    variant="outlined"
                    onChange={handleText}/>
                <Button onClick={handleClick}>Add</Button>
            </Box>
            <Show store={
                props.store
            }/>
        </div>
    );
}
