const initState = [{
        id: 0,
        content: 'this is a test',
        visibility: true
    }];

function reducer(state = initState, action) {
    switch (action.type) {
        case 'ADD':
            return [
                ... state,
                action.item
            ];
        case 'DEL':
            let purged = Object.assign([], state)
            return [
                ...purged.slice(0, action.id),
                ...purged.slice(action.id+1)
]
        case 'EDIT':
            console.log('editing this item...')
            break;
        default:
            return state;
    }
}
export default reducer;
