const initState = [{
        id: 0,
        content: 'this is a test',
        visibility: true
    }];

function reducer(state = initState, action) {
    switch (action.type) {
        case 'ADD':
            return [
                ... state,
                action.item
            ];
        case 'DEL':
            let purged = [];
            purged = purged.concat(state);
            return purged.filter(item => (item.id !== action.id))
        case 'EDIT':
            let edited = [];
            edited = edited.concat(state);
            edited.forEach(elm => {
                if(elm.id === action.item.id){
                    let elmEdited = Object.assign({}, elm, {content:action.item.content}) 
                    elm = elmEdited
                    console.log(elm, elmEdited)
                    return;               
                }
            })
            console.log(edited)
            return edited;
        default:
            return state;
    }
}
export default reducer;
