import React from 'react';

import Typography from '@material-ui/core/Typography';
import {makeStyles} from '@material-ui/core/styles';
import {Card, CardContent, CardActions, Button, TextField} from '@material-ui/core';

const useStyless = makeStyles(theme => ({
    root: {
        padding: theme.spacing(3, 2),
        backgroundColor: '#607d8b',
        color: 'white',
        marginTop: '10px',
        display:'flex',
        flexDirection: 'row',
        justifyContent: 'space-around'
    }
}));

const Content = (item, handleChange) => {
    return (
        <TextField id="normal-textarea" label="ToDo ..." placeholder="Memorizable thing ;)"
                value={item.content}
                margin="normal"
                />
    );
}
const Cart = (isEditing, item) => {
    const classes = useStyless();
    return (
        <Box position="static" className={classes.root}>
            <input type="checkbox" />
            {isEditing ? content: item.content}
        </Box>
    );
}

export default Cart;
