const initState = [
    {
        id: 0,
        title: "this is Title",
        text: "this is content"
    },
];

function  doThis (state = initState, action ){
    console.log(action, action.type);
    switch(action.type){
        case 'ADD_TODO':
            let updated = Object.assign([], state);
            updated.push(action.item);
            return updated;
        case 'DELETE_TODO':
            let reducet = Object.assign([], state);
            reducet = reducet.filter(item => (item.id !== action.id));
            return reducet;
        case 'EDIT_TODO':
            let edited = Object.assign([], state);
            edited.forEach((item, i) => {
                if( action.item.id === i ){
                    item.title = (action.item.title)? item.title : action.item.title ;
                    item.text = (action.item.text)? item.text : action.item.text ;
                }
            });
            return edited;
        default:
            return state;
    }
}
export default doThis;
