const initState = [{
        id: 0,
        content: 'this is a test',
        visibility: true
    }];

function reducer(state = initState, action) {
    switch (action.type) {
        case 'ADD':
            return [
                ... state,
                action.item
            ];
        case 'DEL':
            return state.filter(item => (item.id !== action.id));
        case 'EDIT':
            console.log('editing this item...')
            break;
        default:
            return state;
    }
}
export default reducer;
