import React, {useState} from 'react';
import Container from '@material-ui/core/Container';
import {makeStyles} from '@material-ui/core/styles';

import Cart from './cart';
import add from '../actions/add';
import del from '../actions/delete';
import edit from '../actions/edit';
import {
    TextField,
    Card,
    CardContent,
    Typography,
    Button,
    CardActions
} from '@material-ui/core';

const Show = props => {

    function componentDidMount() {
        props.store.subscribe(() => console.log(props.store.getState()))
    }

    const useStyless = makeStyles(theme => ({
        padd: {
            padding: theme.spacing(3, 2)
        }
    }));

    function del_todo(id) {
        props.store.dispatch(del(id))
    };
    function edit_todo(item) {
        props.store.dispatch(edit(item))
    };

    const [title, setTitle] = useState('');
    const [text, setText] = useState('');

    const classes = useStyless();

    return (
        <React.Fragment>
            <Container className={
                classes.padd
            }>
                <Card className={
                    classes.root
                }>
                    <CardContent>
                        <Typography component="h3" variant="h5">
                            {'Add an Element'} </Typography>
                        <TextField required id="outlined-textarea" label="Title" placeholder="Memorizable thing ;)"
                            value={title}
                            margin="normal"
                            variant="outlined"
                            onChange={
                                event => setTitle(event.target.value)
                            }/>
                        <TextField required id="outlined-textarea" label="Text" multiline placeholder="things to remember!"
                            value={text}
                            variant="outlined"
                            onChange={
                                event => setText(event.target.value)
                            }/>
                    </CardContent>
                    <CardActions>
                        <Button color="inherit"
                            onClick={
                                () => {
                                    console.log({id: props.store.getState().length, title, text})
                                    props.store.dispatch(add({id: props.store.getState().length, title, text}))
                                }
                        }>
                            Add
                        </Button>
                    </CardActions>
                </Card>
                {
                props.store.getState().map((item, i) => (
                    <Cart handleDel={del_todo}
                        handleEdit={edit_todo}
                        item
                        ={item}
                        key={i}/>
                ))
            } </Container>
        </React.Fragment>
    );
}

export default Show;
