import React from 'react';
import Container from '@material-ui/core/Container';
import {makeStyles} from '@material-ui/core/styles';

import Cart from './cart';
import del from '../actions/delete';
import edit from '../actions/edit';

const Show = props => {
    const useStyless = makeStyles(theme => ({
        padd: {
            padding: theme.spacing(3, 2)
        },
    }));

    const DEL_TODO = id => props.store.dispatch(del(id)) ;
    const EDIT_TODO = item => props.store.dispatch(edit(item));

    const classes = useStyless();

    return (
        <React.Fragment>
            <Container className={
                    classes.padd
                }
                >

                {
                props.store.getState().map((item, i) => (
                    <Cart handleDel={DEL_TODO} handleEdit={EDIT_TODO} item ={item} key={i}/>
                ))
            } </Container>
        </React.Fragment>
    );
}

export default Show;
