const initState = [{
        id: 0,
        content: 'this is a test',
        visibility: true
    }];

function reducer(state = initState, action) {
    switch (action.type) {
        case 'ADD':
            return [
                ... state,
                action.item
            ];
        case 'DEL':
            let purged = [];
            purged = purged.concat(state);
            return purged.filter(item => (item.id !== action.id))
        case 'EDIT':
            let edited = [];
            edited = edited.concat(state);

            for (elm of edited)
                if(elm.id === action.item.id)
                    elm = action.item;

            return edited;
        default:
            return state;
    }
}
export default reducer;
