import React, {useState} from 'react';
import Container from '@material-ui/core/Container';
import {makeStyles} from '@material-ui/core/styles';

import ToDo from './todo';
import {addTodo} from '../actions/index';
import {
    TextField,
    Button,
    Box
} from '@material-ui/core';

const Show = props => {

    function componentDidMount() {
        props.store.subscribe(() => console.log(props.store.getState()))
    }
    const [elm, setElm] = useState('');

    const useStyless = makeStyles(theme => ({
        padd: {
            padding: theme.spacing(3, 2),
            display: 'flex',
            justifyContent: 'center',
            flexDirection: 'column',
            alignItems: 'center'
        }
    }));

    const classes = useStyless();
    const handelChange = event => {

    }
    const handleDel = id => {

    }
    return (
        <React.Fragment>
            <Container className={
                classes.padd
            }>
                <Box position='static' display='flex' flexDirection='row'>
                    <TextField id="normal-textarea" label="ToDo ..." placeholder="Memorizable thing ;)"
                        value={elm}
                        margin="normal"
                        onChange={
                            event => {
                                setElm(event.target.value);
                            }
                        }/>
                    <Button variant='outlined' size="small" color='primary' 
                        onClick={
                            () => {
                                props.store.dispatch(addTodo({id: props.store.getState().length, content: elm, visiblity: true}));
                                setElm('')
                            }
                    }>
                        Add
                    </Button>
                </Box>
                {
                props.store.getState().map((item, i) => {
                    return (
                        <ToDo item={item}
                            key={i}
                            isEditing={false}
                            handelChange={handelChange}
                            handleEdit={handleEdit}
                            handleDel={handleDel}/>
                    )
                })
            } </Container>
        </React.Fragment>
    );
}

export default Show;
